    private void BingresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BingresarActionPerformed
        // TODO add your handling code here:
        Bingresar.setEnabled(false);

        Binfo.setVisible(false);
        Bretirar.setVisible(false);
        Binventario.setVisible(false);
        IngreReti.setVisible(false);
        String CodigoText = Codigo.getText();
        String ingretiText = Ingreti.getText();
        
        if (!CodigoText.isEmpty() && !ingretiText.isEmpty()) {
    try {
        int op = Integer.parseInt(Codigo.getText());
        int can = Integer.parseInt(Ingreti.getText());
        int canDama= General1.cantidad() + can;
        int canCaballero =  General2.cantidad() + can;
        int canNino = General3.cantidad() + can;
        // TODO add your handling code here:
       switch(op){
    case 1:
        General1.agregarCantidad(can);
        Respuesta.setText("Ha elegido ingresar cantidad a Dama\nCantidad disponible: \"" + General1.cantidad());
        break;
    case 2:
        General2.agregarCantidad(can);
        Respuesta.setText("Ha elegido ingresar cantidad a Caballero\nCantidad disponible: \"" + General2.cantidad());
        break;
    case 3:
        General3.agregarCantidad(can);
        Respuesta.setText("Ha elegido ingresar cantidad a niños\nCantidad disponible: \"" + General3.cantidad());
        break;
}

        } catch (NumberFormatException e) {
        // Manejo del caso cuando el campo de entrada no es un número
        Respuesta.setText("Por favor, ingrese un número válido.");
    }
} else {
    // Manejo del caso cuando el campo de entrada está vacío
    Respuesta.setText("Por favor, no deje los campos vacíos.");
        }
    }//GEN-LAST:event_BingresarActionPerformed

