    private void BretirarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BretirarActionPerformed
        // TODO add your handling code here:
        Bretirar.setEnabled(false);
        Bingresar.setVisible(false);
        Binfo.setVisible(false);
        Binventario.setVisible(false);
        IngreReti.setVisible(false);
         
        int op;
        op = Integer.parseInt(Codigo.getText());
        int can;
        can = Integer.parseInt(IngreReti.getText());
        int canDama= General1.cantidad() - can;
        int canCaballero =  General2.cantidad() - can;
        int canNino = General3.cantidad() - can;
        // TODO add your handling code here:
        switch(op){
            case 001 : 
                Respuesta.setText( """
                                                    Ha elegido retirar cantidad a  Dama
                                                    Cantidad disponible: """ + canDama);
                break;
            case 002 :
                Respuesta.setText( """
                                                    Ha elegido retirar cantidad a Caballero
                                                    Cantidad disponible: """ + canCaballero);
                break;
            case 003 : 
                Respuesta.setText( """
                                                    Ha elegido retirar cantidad a niños
                                                    Cantidad disponible: """  + canNino);
                break;
        }
        
    }//GEN-LAST:event_BretirarActionPerformed

    private void BinfoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BinfoActionPerformed
        Binventario.setVisible(false);
        Bingresar.setVisible(false);
        Bretirar.setVisible(false);
        Binfo.setEnabled(false);
        
        
        int op;
        op = Integer.parseInt(Codigo.getText());
        switch(op){

            case 001 : 
                Respuesta.setText ("Informacion de Caballero" + General2.marca() + General2.Color() + General2.talla() + General2.ubicacion() + General2.codigo());

                break;
            case 002 :
                Respuesta.setText ("Informacion de Dama" + General1.marca() + General1.Color() + General1.talla() + General1.ubicacion() + General1.codigo());
                break;
            case 003 : 
                Respuesta.setText ("Informacion de Niño" + General3.marca() + General3.Color() + General3.talla() + General3.ubicacion() + General3.codigo());

                break;
        }  
    }//GEN-LAST:event_BinfoActionPerformed

